package com.network.networkimport com.network.models.ModelChapterHadith3import com.network.models.ModelHadithBooksimport com.network.models.ModelHadithChapter2import com.network.models.ModelHadithDetail4import com.network.models.ModelNextPreviousimport retrofit2.Responseimport retrofit2.http.Bodyimport retrofit2.http.Fieldimport retrofit2.http.FormUrlEncodedimport retrofit2.http.GETimport retrofit2.http.POSTimport retrofit2.http.Queryinterface ApiInterfaceTaibahIslamic {   //create endpoints for quran hadith    @GET("gethadithbook")    suspend fun getHadithBooks1(    ): Response<ModelHadithBooks>    @FormUrlEncoded    @POST("getchaptersbybookid")    suspend fun getHadithChapters2(        @Query("pageno") pageno : Int,        @Field("book_id") book_id : String,        @Field("total_pages") total_pages : String,    ): Response<ModelHadithChapter2>    @FormUrlEncoded    @POST("gethadithbychapterid")    suspend fun getChapterHadiths3(        @Query("pageno") pageno : Int,        @Field("chapter_id") chapter_id : String,        ): Response<ModelChapterHadith3>    @FormUrlEncoded    @POST("gethadithbyid")    suspend fun getHadithDetail(        @Field("id") id : String,    ): Response<ModelHadithDetail4>    @FormUrlEncoded    @POST("nextPreviousHadith")    suspend fun nextPreviousHadith(        @Field("chapter_id") chapter_id: String,        @Field("hadith_id") hadith_id : String,        @Field("isNext") isNext :Boolean    ): Response<ModelNextPrevious>}